import "./App.css";
import contacts from "./contacts.json";
import { useState } from "react";

function App() {
  const contactsArray = contacts.splice(0, 5);
  const [list, setList] = useState(contactsArray);
  const addContact = () => {
    const additionalContact = [...contacts]
      .sort(() => 0.5 - Math.random())
      .slice(0, 1);
    if (list.map((element) => element.id).includes(additionalContact[0].id)) {
      return;
    } else {
      setList([...list, additionalContact[0]]);
    }
  };

  const sortPop = () => {
    const copyArray = [...list];
    copyArray.sort((first, second) => {
      return second.popularity - first.popularity;
    });
    setList(copyArray);
  };
  const sortName = () => {
    const copyNameArray = [...list];
    copyNameArray.sort((first, second) => {
      if (first.name < second.name) {
        return -1;
      } else {
        return 1;
      }
    });
    setList(copyNameArray);
  };
  const deleteContact = (contactId) => {
    const deleteArray = list.filter((element) => element.id !== contactId);
    setList(deleteArray);
  };

  return (
    <div className="App">
      <div className="title">
        <h1>IronContacts</h1>
        <div className="buttons">
          <button onClick={() => addContact()}>Add Random Contact</button>
          <button onClick={() => sortPop()}>Sort by popularity</button>
          <button onClick={() => sortName()}>Sort by name</button>
        </div>
      </div>
      <table className="contactTable">
        <thead>
          <tr>
            <th className="imageShift">Picture</th>
            <th>Name</th>
            <th>Popularity</th>
            <th>Won an Oscar</th>
            <th>Won an Emmy</th>
            <th>Actions</th>
          </tr>
        </thead>
        {list.map((element) => {
          return (
            <tbody key={element.id}>
              <tr>
                <td>
                  <img
                    className="contactImage"
                    src={element.pictureUrl}
                    alt="ContactImage"
                  />
                </td>
                <td>{element.name}</td>
                <td>{element.popularity.toFixed(2)}</td>
                {element.wonOscar ? (
                  <td>
                    <img src="data:image/png;base64," />
                  </td>
                ) : (
                  <td></td>
                )}
                {element.wonEmmy ? (
                  <td>
                    <img src="data:image/png;base64," />
                  </td>
                ) : (
                  <td></td>
                )}
                <td>
                  <button onClick={() => deleteContact(element.id)}>
                    Delete
                  </button>
                </td>
              </tr>
            </tbody>
          );
        })}
      </table>
    </div>
  );
}

export default App;
